// Custom Javascript
        var numTopics = -1;
        var topicVisibile = [];
        var topics = ['setupRpi','setupDI','connectRpi', 'setupNodejs','setupMQTT','setupMongoDB', 'demoCircuit', 'deviceController', 'deviceMessageAdapter', 'controlBox', 'customApp'];
        $( document ).ready(function()
        {
            numTopics = topics.length;
            for (var ii = 0; ii < numTopics; ++ii)
            {
                $('#' + topics[ii]).hide();
                topicVisibile[ii] = false;
            }
        });
        function getTopicIndex(topic)
        {
            var itopic = -1;
            for (var ii = 0; ii < numTopics; ++ii)
            {
                if (topic == topics[ii])
                {
                    itopic = ii;
                    ii = numTopics;
                }
            }
            return itopic;
        }
        function topicClicked(topic)
        {
            var itopic = getTopicIndex(topic);
            if (topicVisibile[itopic])
            {
                $('#' + topics[itopic]).hide();
                $('#' + topics[itopic] + 'Button').html(' (learn more)');
                topicVisibile[itopic] = false;
            }
            else
            {
                $('#' + topics[itopic]).show();
                $('#' + topics[itopic] + 'Button').html(' (show less)');
                topicVisibile[itopic] = true;
            }
        }

